mapper.update:
UPDATE tbl_board
SET title=#{title}, content=#{content}, regdate=CURRENT_TIMESTAMP
WHERE bno=#{bno}

update tbl_board
set viewcnt=viewcnt+1
where bno=#{bno}

mapper.select:
select * from tbl_board
order by bno desc 
limit #{pageStart}, #{perPageNum}

select * from tbl_board

order by bno desc 
limit #{pageStart}, #{perPageNum}
